    class ImagenFondo extends JPanel{
       
            @Override
            
            public void paint(Graphics g){
                ImageIcon imagen = new ImageIcon(getClass().getResource("java.png"));
                g.drawImage(imagen.getImage(), 0, 0, getWidth(), getHeight(), this);
                setOpaque(false);
                super.paint(g);
            }
   }   
    
